endpoints.example:
{
    "tenant": "<tenant>",
    "zone" : "<name of zone>",
    "action" : "add|delete",
    "protected_membership": null,
    "endpoints" : [
        {
            "action" : "add|delete",
            "ip" : "<ip address>|<network>",
            "name" : "<name>",
            "segment": null
        },
        {
        ... repeat endpoint requests
        }
    ],
    "tags" : {
        "bu" : "<bu>",
        "bu_line" : "<bu line>",
        "prod_nonprod" : "prod|nonprod",
        "project" : "<project>",
        "tier" : "<tier>",
        "type" : "<type>"
    }
}

{
    "wip": "<wide ip fqdn>",
    "lb_method": "[global-availability|round-robin]",
    "lb_port": 443,
    "loadbalancers": [
        { "site": "<dc1>",
          "fqdn": "<dc1 loadbalancer fqdn>",
          "order": <zero based integer>
        },
        { "site": "<dc2>",
          "fqdn": "<dc2 loadbalancer fqdn",
          "order": <integer>
        }
    ]
}

{
    "devices":
    [
        {
            "site": "<site>",
            "fabric": "<fabric>",
            "location": "<rack location>",
            "action": "add",
            "device_type": "<device type>",
            "hostname": "<hostname>",
            "mgmt": true|false,
            "mgmt_mac": "00:00:00:00:00:00",
            "inf_count": <interface count>,
            "console": true|false,
            "manual_port_selection": true|false,
            "mlag_ports": [
            {
                "vlans": "201",
                "interfaces": [
                    {
                    "inf_name": "nic1",
                    "bridge_mode": "trunk",
                    "cumulus_switch": {
                        "sw": "abcsv1-cloud",
                        "port": "swp30"
                        }
                    },
                    {
                    "inf_name": "nic2",
                    "bridge_mode": "trunk",
                    "cumulus_switch": {
                        "sw": "abcsv2-cloud",
                        "port": "swp30"
                        }
                    }
                ]
            }
        ],
        "standalone_ports": [
            {
                "inf_name": "nic3",
                "bridge_mode": "trunk",
                "vlans": "202",
                "cumulus_switch": {
                    "sw": "abcsv1-cloud",
                    "port": "swp21"
                    }
            },
            {
                "inf_name": "nic4",
                "bridge_mode": "trunk",
                "vlans": "201,202",
                "cumulus_switch": {
                    "sw": "abcsv2-cloud",
                    "port": "swp21"
                    }
            }
            ]
        }
    ]
}
